        <title>Loan Calculator</title>
        <!-- Bootstrap CSS -->
        <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/css/bootstrap.min.css">
        <style>
            body {
                font-family: Arial, sans-serif;
                margin: 0;
                padding: 0;
                background-color: #f2f2f2;
            }

            .container {
                max-width: 500px;
                margin: 50px auto;
                background-color: #fff;
                padding: 30px;
                border-radius: 8px;
                box-shadow: 0 0 20px rgba(0, 0, 0, 0.1);
            }

            h1 {
                text-align: center;
                margin-bottom: 30px;
                color: #007bff;
            }

            form {
                margin-bottom: 20px;
            }

            .form-group {
                margin-bottom: 25px;
            }

            label {
                font-weight: bold;
            }

            .btn-primary {
                background-color: #007bff;
                color: #fff;
                border: none;
                padding: 12px 20px;
                border-radius: 4px;
                cursor: pointer;
                transition: background-color 0.3s;
            }

            .btn-primary:hover {
                background-color: #0056b3;
            }

            .result {
                margin-top: 30px;
                text-align: center;
            }

            .result h2 {
                color: #28a745;
            }
        </style>
    </head>

    <body>



        <!-- Modal -->
        <div class="modal fade" id="loanCalculatorModal" tabindex="-1" role="dialog"
            aria-labelledby="loanCalculatorModalLabel" aria-hidden="true">
            <div class="modal-dialog modal-lg" role="document">
                <div class="modal-content">
                    <div class="modal-header">
                        <h5 class="modal-title" id="loanCalculatorModalLabel">Loan Calculator</h5>
                        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                            <span aria-hidden="true">&times;</span>
                        </button>
                    </div>
                    <div class="modal-body">
                        <form id="loan-form" class="form-inline">
                            <div class="form-group">
                                <label for="loan_amount">Loan Amount:</label>
                                <!-- Added col-sm-8 class to make the input box smaller -->
                                <input type="number" class="form-control col-sm-8 " id="loan_amount"
                                    name="loan_amount" required>
                            </div>
                            <button type="submit" class="btn btn-primary">Calculate</button>
                        </form>

                        <div class="result" id="results">
                            <h2>Results</h2>
                            <table class="table">
                                <thead>
                                    <tr>
                                        <th>Months</th>
                                        <th>Monthly Payment</th>
                                        <th>Total Payment</th>
                                        <th>Total Interest</th>
                                    </tr>
                                </thead>
                                <tbody id="results-body">
                                    <!-- Result rows will be appended here -->
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        <style>
            /* Style for form elements */
            .modal-body form .form-group {
                margin-bottom: 20px;
            }

            /* Style for modal header */
            .modal-header {
                background-color: #f8f9fa;
                border-bottom: 1px solid #dee2e6;
            }

            /* Style for modal title */
            .modal-title {
                color: #343a40;
                font-weight: bold;
            }

            /* Style for close button */
            .close {
                color: #000;
            }

            /* Style for modal content */
            .modal-content {
                background-color: #fff;
                border: 1px solid rgba(0, 0, 0, 0.2);
                border-radius: 5px;
            }

            /* Style for modal body */
            .modal-body {
                padding: 20px;
            }
        </style>
        <div class="modal fade" id="bookModal" tabindex="-1" role="dialog" aria-labelledby="bookModalLabel"
            aria-hidden="true">
            <div class="modal-dialog" role="document">
                <div class="modal-content">
                    <!-- Modal Header -->
                    <div class="modal-header">
                        <h5 class="modal-title" id="bookModalLabel">Book an Appointment</h5>
                        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                            <span aria-hidden="true">&times;</span>
                        </button>
