        <script src="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/js/bootstrap.min.js"></script>
        <script>
            document.getElementById('loan-form').addEventListener('submit', function(e) {
                e.preventDefault();
                calculateLoan();
            });

            function calculateLoan() {
                const loanAmount = parseFloat(document.getElementById('loan_amount').value);
                const interestRate = 0.1; // Example interest rate, you can fetch this dynamically if needed
                const loanTerm = 12; // Fixed to 12 for simplicity

                const monthlyInterestRate = interestRate / 12;
                const resultsBody = document.getElementById('results-body');
                resultsBody.innerHTML = ''; // Clear previous results

                for (let months of [12, 18, 24, 30, 36]) {
                    const x = Math.pow(1 + monthlyInterestRate, months);
                    const monthlyPayment = (loanAmount * x * monthlyInterestRate) / (x - 1);
                    const totalPayment = monthlyPayment * months;
                    const totalInterest = totalPayment - loanAmount;

                    // Create and append table row
                    const row = document.createElement('tr');
                    row.innerHTML = `
                        <td>${months}</td>
                        <td>â‚±${monthlyPayment.toFixed(2)}</td>
                        <td>â‚±${totalPayment.toFixed(2)}</td>
                        <td>â‚±${totalInterest.toFixed(2)}</td>`;
                    resultsBody.appendChild(row);
                }
            }
        </script>
